from decimal import*
getcontext().prec=150
decpi=Decimal(3.)
getcontext().rounding=ROUND_HALF_UP
def decsin(t):
    t=t%(2*decpi)
    v,c,s,u,i,o=Decimal(1),Decimal(0),Decimal(t),Decimal(1),Decimal(t),Decimal(1)
    while s!=c:
        c=s
        v+=2
        u*=v*(v-1)
        i*=t*t
        o*=-1
        s+=i/u*o
    return Decimal(s)
a,s,d=map(Decimal,input().split())
t=Decimal()
e=Decimal(0)
r=Decimal(500000)
w=Decimal(1.0e-60)
while(Decimal(r-e).copy_abs())>=w:
    t=(e+r)/2
    if Decimal(a*t+s*decsin(t))<Decimal(d):
        e=t
    else:
        r=t
print(round(t,9))
